import React, { useState } from 'react';

function Activity(props: any) {
  const [activity, setActivity] = useState('');
  const handleActivitySubmit = () => {
    props.addActivity(props.currentColumn.id, props.currentCard.id, activity);
  };
  return (
    <>
      <div className="card-deatil-title">Activity</div>
      <textarea
        className="card-detail-activity"
        placeholder="Add Comment"
        onChange={(e) => setActivity(e.target.value)}
        value={activity}
      >
        {activity}
      </textarea>
      <div>
        <button
          disabled={activity.length < 1}
          onClick={() => handleActivitySubmit()}
          className="complete-btn cursor-pointer float-right bg-skyblue"
        >
          Say it
        </button>
      </div>
      <div className="actibity-log"></div>
    </>
  );
}
export default Activity;
