import React, { useState } from 'react';

function Activity(props: any) {
  const [activity, setActivity] = useState('');
  const handleActivitySubmit = () => {
    props.addActivity(props.currentColumn.id, props.currentCard.id, activity);
  };
  return (
    <>
      <div className="card-deatil-title">Activity</div>
      <textarea
        className="card-detail-activity"
        placeholder="Add Comment"
        onChange={(e) => setActivity(e.target.value)}
        value={activity}
      >
        {activity}
      </textarea>
      <div className="text-right">
        <button
          onClick={() => handleActivitySubmit()}
          className="complete-btn cursor-pointer bg-head-1 border-none"
        >
          Say it
        </button>
      </div>
      <div className="actibity-log">
        {props.currentCard.activities.map((activity: any) => {
          let iconElement = null;
          switch (true) {
            case activity.activity.includes('checklist'):
              iconElement = <i className="text-skylue far fa-list-alt"></i>;
              break;
          }
          return (
            <div className="activity-row">
              <div className="actibity-icon">{iconElement}</div>
              <div>
                <div>{activity.createdAt.toString()}</div>
                <div>{activity.activity}</div>
              </div>
            </div>
          );
        })}
      </div>
    </>
  );
}
export default Activity;
