import { NextApiRequest, NextApiResponse } from 'next';
import Column from '../../middleware/models/column';
require('../../middleware/db/mongoose');

export default async (request: NextApiRequest, response: NextApiResponse) => {
  const { _id } = request.body;
  console.log(_id);
  try {
    const columns = await Column.aggregate([
      {
        $match: {
          projectId: { $toString: _id },
        },
      },
      {
        $lookup: {
          from: 'cards',
          localField: 'columnId',
          foreignField: '_id',
          as: 'cards',
        },
      },
      {
        $lookup: {
          from: 'checklists',
          localField: 'cardId',
          foreignField: 'cards._id',
          as: 'checklists',
        },
      },
      {
        $lookup: {
          from: 'activities',
          localField: 'cardId',
          foreignField: 'cards._id',
          as: 'activities',
        },
      },
    ]).exec();
    if (!columns) {
      return response.status(404).json({
        success: false,
        message: 'data not found',
      });
    }
    return response.status(200).json({ success: true, data: columns });
  } catch (e) {
    return response
      .status(500)
      .json({ success: false, message: 'failed to get a board data' });
  }
};
