import React from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import Card from './card';

function CardBox(props: any) {
  const grid = 8;

  const getItemStyle = (isDragging: any, draggableStyle: any) => ({
    // some basic styles to make the items look a bit nicer
    userSelect: 'none',
    padding: grid * 2,
    margin: `0 0 ${grid}px 0`,

    // change background colour if dragging
    background: isDragging ? 'lightgreen' : 'grey',

    // styles we need to apply on draggables
    ...draggableStyle,
  });

  return (
    <div {...props.draggableProps}>
      <div className="card-box">
        <div className="subtitle-box h-top bg-skyblue text-white">
          <div
            ref={props.innerRef}
            {...props.dragHandleProps}
            className="icon-box"
          >
            <i className="far fa-lightbulb"></i>
          </div>
          <div className="project-subtitle">Open</div>
          <div className="cards-num">2</div>
        </div>
        <div className="card-body">
          <Droppable droppableId="droppable-2">
            {(provided: any, snapshot: any) => (
              <div ref={provided.innerRef} {...provided.droppableProps}>
                <Draggable key={0} draggableId="0" index={0}>
                  {(provided: any, snapshot: any) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={getItemStyle(
                        snapshot.isDragging,
                        provided.draggableProps.style
                      )}
                    >
                      <Card />
                    </div>
                  )}
                </Draggable>
                <Draggable key={1} draggableId="1" index={1}>
                  {(provided: any, snapshot: any) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={getItemStyle(
                        snapshot.isDragging,
                        provided.draggableProps.style
                      )}
                    >
                      <Card />
                    </div>
                  )}
                </Draggable>
                <Draggable key={2} draggableId="2" index={2}>
                  {(provided: any, snapshot: any) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={getItemStyle(
                        snapshot.isDragging,
                        provided.draggableProps.style
                      )}
                    >
                      <Card />
                    </div>
                  )}
                </Draggable>
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </div>
    </div>
  );
}
export default CardBox;
