import React, { useState, useEffect } from 'react';
import ProjectName from './project-name';
import Disclaimer from './disclaimer';
import Board from './board';
import { useRouter } from 'next/router';

export default function Main() {
  const [view, setView] = useState('board' as string);
  const [projectName, setProjectName] = React.useState('' as any);
  const [isAcceptDisclaimer, setIsAcceptDisclaimer] = useState(false);
  const router = useRouter();
  const { _id, project } = router.query;

  useEffect(() => {
    if (localStorage.getItem('omegakanbanaccept')) {
      setIsAcceptDisclaimer(true);
    }
  });

  let element = null;
  switch (view) {
    case 'project-name':
      element = (
        <ProjectName
          // setView={setView}
          setProjectName={setProjectName}
          projectName={projectName}
        />
      );
      break;
    case 'board':
      element = (
        <Board
          // setView={setView}
          setProjectName={setProjectName}
          projectName={projectName}
        />
      );
      break;
  }

  return (
    <>
      {element}
      {!isAcceptDisclaimer && (
        <Disclaimer setIsAcceptDisclaimer={setIsAcceptDisclaimer} />
      )}
    </>
  );
}
