import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Card from './card';
import Zoom from '@material-ui/core/Zoom';

function CardBody(props: any) {
  const [cards, setCards] = useState([]);
  const [isAdding, setIsAdding] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const [cardTitle, setCardTitle] = useState('');
  const [isCardBoxTileChanging, setIsCardBoxTileChanging] = useState(false);
  const [cardBoxTitle, setCardBoxTitle] = useState('');

  useEffect(() => {
    if (props.cardBoxTitle) {
      setCardBoxTitle(props.cardBoxTitle);
    }
  }, []);

  const getItemStyle = (isDragging: any, draggableStyle: any) => ({
    userSelect: 'none',
    padding: '15px',
    margin: '5px 10px',
    border: isDragging
      ? '2px solid rgb(0, 170, 255)'
      : '1px solid rgb(225,225,225)',
    borderRadius: '10px',
    boxShadow:
      'rgba(0, 0, 0, 0.02) 0px 0px 0px 1px rgba(0, 0, 0, 0.05), 0px 1px 2px 0px, rgba(0, 0, 0, 0.05) 0px 2px 8px 0px',
    background: isDragging ? 'rgb(241,251,255)' : 'white',
    ...draggableStyle,
    height: isDragging ? '100px' : '150px',
  });

  const onDragStart = (result: any) => {
    setIsDragging(true);
    if (cardTitle.length < 1) {
      setIsAdding(false);
    }
  };

  const onDragEnd = (result: any) => {
    setIsDragging(false);
    if (!result.destination) {
      return;
    }
    const items = props.reorder(
      cards,
      result.source.index,
      result.destination.index
    );
    setCards(items as any);
  };

  const handleAddCard = () => {
    setIsAdding(true);
    // setCards([...cards, 1]);
  };

  const handleAddCardBlur = () => {
    if (cardTitle.length < 1) setIsAdding(false);
  };

  const handleCardBoxTitleSubmit = () => {
    if (cardBoxTitle.trim().length > 0) {
      // do something
    }
    setIsCardBoxTileChanging(false);
  };

  const handleKeyDownForCardBoxTitle = (event: any) => {
    if (event.key === 'Enter') {
      handleCardBoxTitleSubmit();
    }
  };

  const handleKeyDownForAddCard = (event: any) => {
    if (event.key === 'Enter') {
      handleCardTitleSubmit();
    }
  };

  const handleCardTitleSubmit = () => {
    console.log(cardTitle);
    if (cardTitle.length > 0) {
      setCards([...cards, { cardTitle }] as any);
      setCardTitle('');
    }
    setIsAdding(false);
  };

  return (
    <div className="card-body">
      <DragDropContext onDragEnd={onDragEnd} onDragStart={onDragStart}>
        <Droppable type="ROW" droppableId="droppable-2" direction="vertical">
          {(provided: any, snapshot: any) => (
            <div ref={provided.innerRef} {...provided.droppableProps}>
              {cards.map((card: any, index) => {
                return (
                  <Draggable
                    key={index}
                    draggableId={index.toString()}
                    index={index}
                  >
                    {(provided: any, snapshot: any) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={getItemStyle(
                          snapshot.isDragging,
                          provided.draggableProps.style
                        )}
                      >
                        <Card cardTitle={card.cardTitle} />
                      </div>
                    )}
                  </Draggable>
                );
              })}
              {isDragging ? (
                ''
              ) : isAdding ? (
                <Zoom in={isAdding}>
                  <div className="h-top flex-center mb-25px">
                    <input
                      className="box-shadow-1 w-100per add-card-title"
                      value={cardTitle}
                      autoFocus
                      onBlur={() => handleAddCardBlur()}
                      onKeyDown={(event) => handleKeyDownForAddCard(event)}
                      onChange={(e) => setCardTitle(e.target.value)}
                    ></input>
                  </div>
                </Zoom>
              ) : (
                <div className="h-top flex-center mb-25px cursor-pointer">
                  <i
                    onClick={() => handleAddCard()}
                    className="add-card-icon box-shadow-1 fas fa-plus"
                  ></i>
                </div>
              )}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}
export default CardBody;
