import React, { useState, useEffect } from 'react';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import { notEqual } from 'assert';
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    modal: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    backdrop: {
      backgroundColor: 'rgb(144,144,144,0.6)',
    },
  })
);

function CardModal(props: any) {
  const classes = useStyles();
  const [cardDetailTitle, setCardDetailTitle] = useState(
    props.currentCard.cardTitle
  );
  const [note, setNote] = useState('');
  const [isNoting, setIsNoting] = useState(false);

  const handleKeyDown = (event: any) => {
    if (event.key === 'Enter') {
      console.log(event.target);
    }
  };

  return (
    <>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={props.open}
        onClose={props.handleModalClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
          classes: {
            root: classes.backdrop,
          },
        }}
      >
        <Fade in={props.open}>
          <div className="modal-paper">
            <div className="card-detail-header">
              <button className="complete-btn cursor-pointer">Complete</button>
            </div>
            <div className="card-detail-body">
              <div className="card-detail-left">
                <div id="transition-modal-title">
                  <input
                    id={props.currentCard.id}
                    className="card-deatil-title border-none"
                    type="text"
                    onChange={(e) => setCardDetailTitle(e.target.value)}
                    value={cardDetailTitle}
                    onKeyDown={(e) => handleKeyDown(e)}
                  />
                </div>
                <div>
                  {isNoting ? (
                    <input
                      id={props.currentCard.id}
                      className="card-deatil-note-input border-none"
                      type="text"
                      autoFocus
                      onChange={(e) => setNote(e.target.value)}
                      value={note}
                      onKeyDown={(e) => handleKeyDown(e)}
                      onBlur={() => setIsNoting(false)}
                    />
                  ) : note.length < 1 ? (
                    <div
                      onClick={() => setIsNoting(true)}
                      className="card-detail-note cursor-text"
                    >
                      This task has no notes.
                    </div>
                  ) : (
                    <div
                      onClick={() => setIsNoting(true)}
                      className="card-detail-note cursor-text"
                    >
                      {note}
                    </div>
                  )}
                  <div className="card-deatil-title">Checklist</div>
                  <div className="card-deatil-checklist text-skyblue cursor-pointer">
                    + Add Checklist Item
                  </div>
                  <div className="card-deatil-title">Activity</div>
                  <textarea
                    className="card-detail-activity"
                    placeholder="Add Comment"
                  ></textarea>
                  <div>
                    <button className="complete-btn cursor-pointer float-right">
                      Say it
                    </button>
                  </div>
                  <div className="actibity-log"></div>
                </div>
              </div>
              <div className="card-detail-right bg-grey-1">
                <div>
                  <MuiPickersUtilsProvider utils={DateFnsUtils}>
                    <Grid container justify="space-around">
                      <KeyboardDatePicker
                        disableToolbar
                        variant="inline"
                        format="MM/dd/yyyy"
                        margin="normal"
                        id="date-picker-inline"
                        label="Date picker inline"
                        value={selectedDate}
                        onChange={handleDateChange}
                        KeyboardButtonProps={{
                          'aria-label': 'change date',
                        }}
                      />
                      <KeyboardDatePicker
                        margin="normal"
                        id="date-picker-dialog"
                        label="Date picker dialog"
                        format="MM/dd/yyyy"
                        value={selectedDate}
                        onChange={handleDateChange}
                        KeyboardButtonProps={{
                          'aria-label': 'change date',
                        }}
                      />
                      <KeyboardTimePicker
                        margin="normal"
                        id="time-picker"
                        label="Time picker"
                        value={selectedDate}
                        onChange={handleDateChange}
                        KeyboardButtonProps={{
                          'aria-label': 'change time',
                        }}
                      />
                    </Grid>
                  </MuiPickersUtilsProvider>
                </div>
              </div>
            </div>
          </div>
        </Fade>
      </Modal>
    </>
  );
}
export default CardModal;
