import mongoose from 'mongoose';
const columnSchema = new mongoose.Schema({
  projectId: {
    type: mongoose.Schema.Types.ObjectId,
    required: true,
    ref: 'Router'
  },
  title: {
    type: String,
    required: true,
    trim: true
  },
  cards: [{
    card: {
      type: mongoose.Schema.Types.ObjectId,
      required: false,
      ref: 'Card'
    }
  }],
  colorIndex: {
    type: Number,
    required: true
  }
}, {
  timestamps: true
});
const Column = mongoose.models.Column || mongoose.model('Column', columnSchema);
export default Column;
