import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Card from './card';

function CardBox(props: any) {
  const [cards, setCards] = useState([0]);
  const getItemStyle = (isDragging: any, draggableStyle: any) => ({
    userSelect: 'none',
    padding: '15px',
    margin: '10px',
    border: isDragging
      ? '2px solid rgb(0, 170, 255)'
      : '1px solid rgb(225,225,225)',
    borderRadius: '10px',
    boxShadow: '1px 1px rgb(234, 234, 234)',
    background: isDragging ? 'rgb(241,251,255)' : 'white',
    ...draggableStyle,
  });

  const onDragEnd = (result: any) => {
    // dropped outside the list
  };

  return (
    <div {...props.draggableProps}>
      <div className="card-box">
        <div className="subtitle-box h-top bg-skyblue text-white">
          <div
            ref={props.innerRef}
            {...props.dragHandleProps}
            className="icon-box"
          >
            <i className="far fa-lightbulb"></i>
          </div>
          <div className="project-subtitle">Open</div>
          <div className="cards-num">2</div>
        </div>
        <div className="card-body">
          <DragDropContext onDragEnd={onDragEnd}>
            <Droppable droppableId="droppable-2">
              {(provided: any, snapshot: any) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  {cards.map((card, index) => {
                    <Draggable
                      key={index}
                      draggableId={index.toString()}
                      index={index}
                    >
                      {(provided: any, snapshot: any) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          style={getItemStyle(
                            snapshot.isDragging,
                            provided.draggableProps.style
                          )}
                        >
                          <Card />
                        </div>
                      )}
                    </Draggable>;
                  })}
                  <div className="add-card-icon">
                    <i className="fas fa-plus"></i>
                  </div>
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </DragDropContext>
        </div>
      </div>
    </div>
  );
}
export default CardBox;
