import React, { useState } from 'react';

function CardDetailHead(props: any) {
  const [cardDetailTitle, setCardDetailTitle] = useState(() => {
    const card = props.columns[props.currentColumnId].cards.filter(
      (card: any) => {
        return card.id === props.currentCardId;
      }
    );
    console.log(card);
    return card.cartTitle;
  });

  const handleKeyDown = (e: any) => {
    if (e.key === 'Enter') {
      props.updateCardTitle(
        props.currentColumnId,
        props.currentCardId,
        cardDetailTitle
      );
    }
  };

  return (
    <div id="transition-modal-title">
      <input
        id={props.currentCardId}
        className="card-deatil-title border-none"
        type="text"
        onChange={(e) => setCardDetailTitle(e.target.value)}
        value={cardDetailTitle}
        onKeyDown={(e) => handleKeyDown(e)}
      />
    </div>
  );
}
export default CardDetailHead;
