import React, { useState } from 'react';

function ChecklistItem(props: any) {
  const [checklist, setChecklist] = useState(props.checklist.checklist);
  const handleChecklistSubmit = (e: any) => {
    if (e.key === 'Enter') {
      props.updateChecklist(
        props.currentColumnId,
        props.currentCardId,
        props.checklistId,
        checklist
      );
      props.addActivity(props.currentColumnId, props.currentCardId, checklist);
    }
  };
  return (
    <div
      key={props.checklistId}
      id={props.checklistId}
      className="display-flex checklist-row"
    >
      <div className="checklist-circle">
        <i className="far fa-circle"></i>
      </div>
      <input
        value={checklist}
        onChange={(e) => setChecklist(e.target.value)}
        onKeyDown={(e) => handleChecklistSubmit(e)}
        className="checklist-content border-none"
      ></input>
    </div>
  );
}
export default ChecklistItem;
