import mongoose from 'mongoose';
const columnSchema = new mongoose.Schema({
  projectId: {
    type: mongoose.Schema.Types.ObjectId,
    required: true,
    ref: 'Router'
  },
  title: {
    type: String,
    required: true,
    trim: true
  },
  colorIndex: {
    type: Number,
    required: true
  },
  category: {
    type: String,
    required: true,
    default: 'normal'
  }
}, {
  timestamps: true
});
const Column = mongoose.models.Column || mongoose.model('Column', columnSchema);
export default Column;
