import React, { useState } from 'react';
import ChecklistItem from './checklistitem';
import { withStyles } from '@material-ui/core/styles';
import Menu, { MenuProps } from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import ListItemText from '@material-ui/core/ListItemText';

const StyledMenu = withStyles({
  paper: {
    border: '1px solid rgb(239, 243, 244)',
  },
})((props: MenuProps) => (
  <Menu
    elevation={0}
    getContentAnchorEl={null}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'center',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'center',
    }}
    {...props}
  />
));

function Checklist(props: any) {
  const [checklist, setChecklist] = useState('');
  const [addChecklist, setAddChecklist] = useState(false);
  const [showAngle, setShowAngle] = useState(false);
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);

  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleSubmitChecklist = (e: any) => {
    if (e.key === 'Enter') {
      if (checklist.trim().length > 0) {
        props.addChecklist(
          props.currentColumn.id,
          props.currentCard.id,
          checklist
        );
      }
      setChecklist('');
    }
  };

  const handleDeleteColClick = () => {};

  return (
    <>
      <div className="card-deatil-title">Checklist</div>
      <div>
        {props.currentCard &&
          props.currentCard.checklists.map((checklist: any) => {
            return (
              <div key={checklist.id}>
                <ChecklistItem
                  currentCard={props.currentCard}
                  currentColumn={props.currentColumn}
                  checklist={checklist}
                  updateChecklist={props.updateChecklist}
                  addActivity={props.addActivity}
                  columns={props.columns}
                  completeChecklist={props.completeChecklist}
                />
              </div>
            );
          })}
      </div>
      {addChecklist && (
        <div>
          <div
            onMouseOver={() => setShowAngle(true)}
            onMouseLeave={() => setShowAngle(false)}
            className="display-flex checklist-row"
          >
            <div className="checklist-circle cursor-pointer">
              <i className="far fa-circle"></i>
            </div>
            <input
              autoFocus
              value={checklist}
              onBlur={(e: any) => {
                if (e.target.value.trim().length === 0) setAddChecklist(false);
              }}
              onChange={(e) => setChecklist(e.target.value)}
              onKeyDown={(e) => handleSubmitChecklist(e)}
              className="checklist-content border-none"
            ></input>
            {showAngle && (
              <>
                <i onClick={handleClick} className="fas fa-angle-down"></i>
              </>
            )}
            <StyledMenu
              id="delete-menu"
              anchorEl={anchorEl}
              keepMounted
              open={Boolean(anchorEl)}
              onClose={() => setAnchorEl(null)}
            >
              <MenuItem onClick={() => handleDeleteColClick()}>
                <ListItemText primary="Delete" />
              </MenuItem>
            </StyledMenu>
          </div>
        </div>
      )}
      <div
        onClick={() => setAddChecklist(true)}
        className="card-deatil-checklist text-skyblue cursor-pointer"
      >
        + Add Checklist Item
      </div>
    </>
  );
}
export default Checklist;
