import React, { useState } from 'react';
import Zoom from '@material-ui/core/Zoom';

function AddSection(props: any) {
  const [isAdding, setIsAdding] = useState(false);
  const [sectionTitle, setSectionTitle] = useState('');

  const handleSectionTitleSubmit = () => {};

  const handleSectionTitleBlur = () => {
    setIsAdding(false);
  };

  return (
    <>
      <div className="card-box">
        {isAdding ? (
          <Zoom in={isAdding}>
            <>
              <div className="icon-box">
                <i className="fas fa-plus"></i>
              </div>
              <form
                className="h-top flex-center w-90per"
                onSubmit={() => handleSectionTitleSubmit()}
              >
                <input
                  className="w-100per change-project-title"
                  value={sectionTitle}
                  autoFocus
                  onBlur={() => setIsAdding(false)}
                  onChange={(e) => setSectionTitle(e.target.value)}
                ></input>
              </form>
            </>
          </Zoom>
        ) : (
          <>
            <div
              onClick={() => {
                setIsAdding(true);
              }}
              className="add-section h-top text-skyblue cursor-pointer"
            >
              <div className="icon-box">
                <i className="fas fa-plus"></i>
              </div>
              <span>Add Section</span>
            </div>
            <div className="card-body">1111</div>
          </>
        )}
      </div>
    </>
  );
}
export default AddSection;
